create procedure BBDW.[USP_CONSTITUENTSUMMARYREVENUE_EXT_PROCESS](@FILTERID uniqueidentifier, @ETLCONTROLID int) as
--Get Selection information declare @FILTERDIMID int, @SELECTIONTABLE nvarchar(255); select @FILTERDIMID = [IDSETREGISTERDIMID], @SELECTIONTABLE = [DBTABLENAME] from bbdw.[DIM_IDSETREGISTER] where [IDSETREGISTERSYSTEMID] = @FILTERID
--Get Processed Date declare @PROCESSEDDATEDIMID int, @PROCESSEDDATE datetime; select @PROCESSEDDATEDIMID = [DATEDIMID], @PROCESSEDDATE = [ACTUALDATE] from bbdw.[DIM_DATE] where [ISCURRENTDATE] = 1;
declare @RANKSQL nvarchar(max);
set @RANKSQL = 'insert into bbdw.[FACT_CONSTITUENTSUMMARYREVENUERANKS_STAGE_EXT]' + char(13) + '(' + char(13) + ' [FINANCIALTRANSACTIONLINEITEMFACTID]' + char(13) + ' ,[LARGEST_RANK]' + char(13) + ' ,[SMALLEST_RANK]' + char(13) + ' ,[FIRST_RANK]' + char(13) + ' ,[LAST_RANK]' + char(13) + ')' + char(13) + 'select' + char(13) + ' li.[FINANCIALTRANSACTIONLINEITEMFACTID]' + char(13) + ' ,row_number() over(partition by li.[CONSTITUENTDIMID] order by li.[FINANCIALTRANSACTIONLINEITEMAMOUNT] desc, li.[FINANCIALTRANSACTIONDATEDIMID] asc) [LARGEST_RANK]' + char(13) + ' ,row_number() over(partition by li.[CONSTITUENTDIMID] order by li.[FINANCIALTRANSACTIONLINEITEMAMOUNT] asc, li.[FINANCIALTRANSACTIONDATEDIMID] asc) [SMALLEST_RANK]' + char(13) + ' ,row_number() over(partition by li.[CONSTITUENTDIMID] order by li.[FINANCIALTRANSACTIONDATEDIMID] asc, li.[ADDEDDATEDIMID] asc) [FIRST_RANK]' + char(13) + ' ,row_number() over(partition by li.[CONSTITUENTDIMID] order by li.[FINANCIALTRANSACTIONDATEDIMID] desc, li.[ADDEDDATEDIMID] desc) [LAST_RANK]' + char(13) + 'from bbdw.[FACT_FINANCIALTRANSACTIONLINEITEM] li' + char(13) + 'inner join dbo.[' + @SELECTIONTABLE + '] fi on fi.[ID] = li.[FINANCIALTRANSACTIONSYSTEMID]';
exec sp_executesql @RANKSQL;
--Do calculations declare @SQL nvarchar(max); set @SQL = 'insert into BBDW.[FACT_CONSTITUENTSUMMARYREVENUE_EXT]' + char(13) + '(' + char(13) + ' [CONSTITUENTDIMID],' + char(13) + ' [CONSTITUENTSYSTEMID],' + char(13) + ' [IDSETREGISTERDIMID],' + char(13) + ' [IDSETREGISTERSYSTEMID],' + char(13) + ' [PROCESSEDDATEDIMID],' + char(13) + ' [PROCESSEDDATE],' + char(13) + ' [FIRSTGIFTAMOUNT],' + char(13) + ' [FIRSTGIFTDATEDIMID],' + char(13) + ' [FIRSTGIFTDATE],' + char(13) + ' [FIRSTGIFTLINEITEMFACTID],' + char(13) + ' [LASTGIFTAMOUNT],' + char(13) + ' [LASTGIFTDATEDIMID],' + char(13) + ' [LASTGIFTDATE],' + char(13) + ' [LASTGIFTLINEITEMFACTID],' + char(13) + ' [LARGESTGIFTAMOUNT],' + char(13) + ' [LARGESTGIFTDATEDIMID],' + char(13) + ' [LARGESTGIFTDATE],' + char(13) + ' [LARGESTGIFTLINEITEMFACTID],' + char(13) + ' [SMALLESTGIFTAMOUNT],' + char(13) + ' [SMALLESTGIFTDATEDIMID],' + char(13) + ' [SMALLESTGIFTDATE],' + char(13) + ' [SMALLESTIFTLINEITEMFACTID],' + char(13) + ' [TOTALGIFTAMOUNT],' + char(13) + ' [AVERAGEGIFTAMOUNT],' + char(13) + ' [GIVINGYEARS],' + char(13) + ' [DISTINCTGIVINGYEARS],' + char(13) + ' [TOTALAPPLICATION],' + char(13) + ' [ISINCLUDED],' + char(13) + ' [ETLCONTROLID],' + char(13) + ' [SOURCEDIMID]' + char(13) + ')' + char(13) + 'select' + char(13) + ' li.[CONSTITUENTDIMID]' + char(13) + ' ,li.[CONSTITUENTSYSTEMID]' + char(13) + ' ,@FILTERDIMID [CONSTITUENTSUMMARYFILTERDIMID]' + char(13) + ' ,@FILTERID [CONSTITUENTSUMMARYFILTERSYSTEMID]' + char(13) + ' ,@PROCESSEDDATEDIMID [PROCESSEDDATEDIMID]' + char(13) + ' ,@PROCESSEDDATE [PROCESSEDDATE]' + char(13) + ' ,max([FIRST].[FINANCIALTRANSACTIONLINEITEMAMOUNT]) [FIRSTGIFTAMOUNT]' + char(13) + ' ,max([FIRST].[FINANCIALTRANSACTIONDATEDIMID]) [FIRSTGIFTDATEDIMID]' + char(13) + ' ,max([FIRST].[FINANCIALTRANSACTIONDATE]) [FIRSTGIFTDATE]' + char(13) + ' ,max([FIRST].[FINANCIALTRANSACTIONLINEITEMFACTID]) [FIRSTGIFTLINEITEMFACTID]' + char(13) + ' ,max([LAST].[FINANCIALTRANSACTIONLINEITEMAMOUNT]) [LASTGIFTAMOUNT]' + char(13) + ' ,max([LAST].[FINANCIALTRANSACTIONDATEDIMID]) [LASTGIFTDATEDIMID]' + char(13) + ' ,max([LAST].[FINANCIALTRANSACTIONDATE]) [LASTGIFTDATE]' + char(13) + ' ,max([LAST].[FINANCIALTRANSACTIONLINEITEMFACTID]) [LASTGIFTLINEITEMFACTID]' + char(13) + ' ,max([LARGEST].[FINANCIALTRANSACTIONLINEITEMAMOUNT]) [LARGESTGIFTAMOUNT]' + char(13) + ' ,max([LARGEST].[FINANCIALTRANSACTIONDATEDIMID]) [LARGESTGIFTDATEDIMID]' + char(13) + ' ,max([LARGEST].[FINANCIALTRANSACTIONDATE]) [LARGESTGIFTDATE]' + char(13) + ' ,max([LARGEST].[FINANCIALTRANSACTIONLINEITEMFACTID]) [LARGESTGIFTLINEITEMFACTID]' + char(13) + ' ,max([SMALLEST].[FINANCIALTRANSACTIONLINEITEMAMOUNT]) [SMALLESTGIFTAMOUNT]' + char(13) + ' ,max([SMALLEST].[FINANCIALTRANSACTIONDATEDIMID]) [SMALLESTGIFTDATEDIMID]' + char(13) + ' ,max([SMALLEST].[FINANCIALTRANSACTIONDATE]) [SMALLESTGIFTDATE]' + char(13) + ' ,max([SMALLEST].[FINANCIALTRANSACTIONLINEITEMFACTID]) [SMALLESTGIFTLINEITEMFACTID]' + char(13) + ' ,sum(li.[FINANCIALTRANSACTIONLINEITEMAMOUNT]) [TOTALGIFTAMOUNT]' + char(13) + ' ,avg(li.[FINANCIALTRANSACTIONLINEITEMAMOUNT]) [AVERAGEGIFTAMOUNT]' + char(13) + ' ,max(d.[CALENDARYEAR]) - min(d.[CALENDARYEAR]) + 1 [GIVINGYEARS]' + char(13) + ' ,count(distinct d.[CALENDARYEAR]) [DISTINCTGIVINGYEARS]' + char(13) + ' ,count(li.[FINANCIALTRANSACTIONLINEITEMFACTID]) [TOTALAPPLICATION]' + char(13) + ' ,1 [ISINCLUDED]' + char(13) + ' ,@ETLCONTROLID [ETLCONTROLID]' + char(13) + ' ,1 [SOURCEDIMID]' + char(13) + 'from bbdw.[FACT_FINANCIALTRANSACTIONLINEITEM] li' + char(13) + 'inner join dbo.[' + @SELECTIONTABLE + '] fi on fi.[ID] = li.[FINANCIALTRANSACTIONSYSTEMID]' + char(13) + 'inner join bbdw.[DIM_DATE] d on d.[DATEDIMID] = li.[FINANCIALTRANSACTIONDATEDIMID]' + char(13) + 'left join bbdw.[FACT_CONSTITUENTSUMMARYREVENUERANKS_STAGE_EXT] [RANKS] on li.[FINANCIALTRANSACTIONLINEITEMFACTID] = [RANKS].[FINANCIALTRANSACTIONLINEITEMFACTID]' + char(13) + 'left join bbdw.[FACT_FINANCIALTRANSACTIONLINEITEM] [LARGEST] on [LARGEST].[FINANCIALTRANSACTIONLINEITEMFACTID] = [RANKS].[FINANCIALTRANSACTIONLINEITEMFACTID] and [RANKS].[LARGEST_RANK] = 1' + char(13) + 'left join bbdw.[FACT_FINANCIALTRANSACTIONLINEITEM] [SMALLEST] on [SMALLEST].[FINANCIALTRANSACTIONLINEITEMFACTID] = [RANKS].[FINANCIALTRANSACTIONLINEITEMFACTID] and [RANKS].[SMALLEST_RANK] = 1' + char(13) + 'left join bbdw.[FACT_FINANCIALTRANSACTIONLINEITEM] [FIRST] on [FIRST].[FINANCIALTRANSACTIONLINEITEMFACTID] = [RANKS].[FINANCIALTRANSACTIONLINEITEMFACTID] and [RANKS].[FIRST_RANK] = 1' + char(13) + 'left join bbdw.[FACT_FINANCIALTRANSACTIONLINEITEM] [LAST] on [LAST].[FINANCIALTRANSACTIONLINEITEMFACTID] = [RANKS].[FINANCIALTRANSACTIONLINEITEMFACTID] and [RANKS].[LAST_RANK] = 1' + char(13) + 'group by li.[CONSTITUENTDIMID], [li].[CONSTITUENTSYSTEMID]'
exec sp_executesql @SQL, N'@FILTERDIMID int , @FILTERID uniqueidentifier, @PROCESSEDDATE datetime, @PROCESSEDDATEDIMID int, @ETLCONTROLID int', @FILTERDIMID = @FILTERDIMID, @FILTERID = @FILTERID, @PROCESSEDDATEDIMID = @PROCESSEDDATEDIMID, @PROCESSEDDATE = @PROCESSEDDATE, @ETLCONTROLID = @ETLCONTROLID;
