create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_DEMO3_CONSTITUENTEDITPERSONALINFORMATION
(
    @ID uniqueidentifier,
    @DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,
	@DEMO3TEXT nvarchar(200) = null output,
    @DEMO3DATE date = null output

)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 1
	set @TSLONG = 0

    select @DEMO3TEXT = VALUE from USR_SYN_CONSTITUENT_DEMO3TEXT where ID = @ID
    select @DEMO3DATE = VALUE from USR_SYN_CONSTITUENT_DEMO3DATE where ID = @ID

	return 0;

SaveImplementation.c:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDIT_DEMO3_CONSTITUENTEDITPERSONALINFORMATION ( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @DEMO3TEXT nvarchar(200), @DEMO3DATE datetime ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try merge dbo.USR_SYN_CONSTITUENT_DEMO3TEXT as target using ( select @ID, @DEMO3TEXT) as source (ID, VALUE) on target.ID = source.ID when MATCHED then UPDATE SET VALUE=source.VALUE ,CHANGEDBYID=@CHANGEAGENTID ,DATECHANGED=@CURRENTDATE when NOT MATCHED then INSERT (ID, VALUE, COMMENT, STARTDATE, ENDDATE, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED) VALUES (ID, source.VALUE, '',null, null, @CHANGEAGENTID, @CHANGEAGENTID, @CURRENTDATE, @CURRENTDATE);
merge dbo.USR_SYN_CONSTITUENT_DEMO3DATE as target using ( select @ID, @DEMO3DATE) as source (ID, VALUE) on target.ID = source.ID when MATCHED then UPDATE SET VALUE=source.VALUE ,CHANGEDBYID=@CHANGEAGENTID ,DATECHANGED=@CURRENTDATE when NOT MATCHED then INSERT (ID, VALUE, COMMENT, STARTDATE, ENDDATE, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED) VALUES (ID, source.VALUE, '',null, null, @CHANGEAGENTID, @CHANGEAGENTID, @CURRENTDATE, @CURRENTDATE);
end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
